# Monoalphabetic Substitution Cipher
from collections import Counter
from operator import itemgetter
import random
import string
print('Encryption with Monoalphabetic Substitution Cipher: ')


def readFile(file):
    f = open(file)
    message = f.read()
    return message


def encrypt(text, key=None):
    alpha = 'abcdefghijklmnopqrstuvwxyz'
    text = text.lower()
    if key is None:
        l = list(alpha)
        random.shuffle(l)
        key = ''.join(l)
    newtext = []
    for letter in text:
        if letter.isalpha():
            newtext.append(key[alpha.index(letter)])
        else:
            newtext += letter
    return [''.join(newtext), key]


paragraph = readFile('text.txt')
print(encrypt(paragraph, None))
