# Caesar-shift via Frequency analysis

print("Frequency Analysis: ")


def Frequency_analyse(ciphertext):
    alphabet = 'abcdefghijklmnopqrstuvwxyz'
    letter_count = {
        'a': 0, 'b': 0, 'c': 0, 'd': 0, 'e': 0, 'f': 0, 'g': 0, 'h': 0,
        'i': 0, 'j': 0, 'k': 0, 'l': 0, 'm': 0, 'n': 0, 'o': 0, 'p': 0, 'q': 0,
        'r': 0, 's': 0, 't': 0, 'u': 0, 'v': 0, 'w': 0, 'x': 0, 'y': 0, 'z': 0
    }
    ciphertext = ciphertext.lower()
    for c in ciphertext:
        if c.isalpha():
            letter_count[c] += 1

    def get_value(letter_count):
        return letter_count[1]
    sorted_text = sorted(letter_count.items(), key=get_value)
    descending_text = list(reversed(sorted_text))
    print(descending_text)
    freq_dict = {1: 'e', 2: 't', 3: 'a', 4: 'o', 5: 'i', 6: 'n', 7: 's', 8: 'h', 9: 'r', 10: 'd', 11: 'l', 12: 'c', 13: 'u',
                 14: 'm', 15: 'f', 16: 'w', 17: 'g', 18: 'y', 19: 'p', 20: 'b', 21: 'v', 22: 'k', 23: 'j', 24: 'x', 25: 'q', 26: 'z'}
    # tinh
    letter_list = []
    cipher_len = 0
    for c in ciphertext:
        if c in alphabet:
            cipher_len += 1
            if c not in letter_list:
                letter_list.append(c)
    letter_freq = []
    for c in letter_list:
        letter_freq.append(
            (c, round(ciphertext.count(c) / cipher_len * 100, 2)))
    letter_freq = sorted(letter_freq, key=itemgetter(1), reverse=True)
    return letter_freq


def getMostCommonChar(text):
    text = text.replace(' ', '')
    return Counter(text).most_common(1)[0][0]


def decrypt(text, jump):
    text = text.lower()
    jump = ord(getMostCommonChar(text))-ord('e')
    alpha = 'abcdefghijklmnopqrstuvwxyz'
    decryption = []
    for letter in text:
        if letter.isalpha():
            ind = alpha.find(letter)
            decryption += alpha[ind - jump]
        else:
            decryption += letter
    return decryption


